    private void jButton22ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton22ActionPerformed
        Set<String> operadoresUnNumero = new HashSet<>(Arrays.asList("PI", "sqrt", "X*2", "X*3", "LOG", "Ln", "SEN", "COS", "TAN", "ARCO", "ARCC", "ARCT"));

            if (!operadoresUnNumero.contains(this.operador)) {
                this.segundonumero = Float.parseFloat(this.CASILLA.getText());
            }


        switch(this.operador){
            case "ARCO":
                if (this.primernumero >= -1 && this.primernumero <= 1) {
                    this.CASILLA.setText(sincero((float) Math.toDegrees(Math.asin(this.primernumero))));
                } else {
                    this.CASILLA.setText("Error: Fuera de rango");
                }
            case "ARCC":
                if (this.primernumero >= -1 && this.primernumero <= 1) {
                    this.CASILLA.setText(Double.toString((float) Math.toDegrees(Math.acos(this.primernumero))));
                } else {
                    this.CASILLA.setText("Error: Fuera de rango");
                }
                break;
            case "|X|":this.CASILLA.setText(sincero(Math.abs(this.primernumero)));break;
            
            case "frac":
            this.CASILLA.setText(convertirAFaccion(this.primernumero));
            break;
            case "ARCT":this.CASILLA.setText(sincero((float)Math.toDegrees(Math.atan(this.primernumero))));break;
            case "SEN":this.CASILLA.setText(sincero((float)Math.sin(Math.toRadians(this.primernumero))));break;
            case "COS":this.CASILLA.setText(Double.toString(Math.cos(Math.toRadians(this.primernumero))));break;
            case "TAN":this.CASILLA.setText(sincero((float)Math.tan(Math.toRadians(this.primernumero))));break;
            case "A/B":
                    if (this.segundonumero == 0) {
                    this.CASILLA.setText("No se puede dividir entre 0");
                    } else {
                    this.CASILLA.setText(sincero(this.primernumero / this.segundonumero));
                    } 
                    break;
            case "%":this.CASILLA.setText(sincero(this.primernumero%this.segundonumero));break;
            case "sqrt":this.CASILLA.setText(Double.toString(Math.sqrt(this.primernumero)));break;
            case "X*2":this.CASILLA.setText(sincero((float)(Math.pow(this.primernumero,2))));break;
            case "X*3":this.CASILLA.setText(sincero((float)(Math.pow(this.primernumero,3))));break;
            case "LOG":this.CASILLA.setText(Double.toString(Math.log(this.segundonumero)/Math.log(this.primernumero)));break;
            case "Ln":this.CASILLA.setText(Double.toString(Math.log(this.primernumero)));break;
            case "+":this.CASILLA.setText(sincero(this.primernumero+this.segundonumero));break;
            case "-":this.CASILLA.setText(sincero(this.primernumero-this.segundonumero));break;
            case "*":this.CASILLA.setText(sincero(this.primernumero*this.segundonumero));break;
            case "/":if(this.segundonumero==0){this.CASILLA.setText("no se puede dividir entre cero");break;}
            else{
                this.CASILLA.setText(sincero(this.primernumero/this.segundonumero));break;
            }
            
            
        }
    }//GEN-LAST:event_jButton22ActionPerformed

