    private void jButton20ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton20ActionPerformed
        String texto = this.CASILLA.getText();

        // Si el texto contiene un punto, lo pasamos a fracción
        if (texto.contains(".")) {
            float valor = Float.parseFloat(texto);
            this.CASILLA.setText(decimalAFraccion(valor));
        }
        // Si el texto contiene una barra, lo pasamos a decimal
        else if (texto.contains("/")) {
            String[] partes = texto.split("/");
            if (partes.length == 2 && esNumero(partes[0]) && esNumero(partes[1])) {
                float numerador = Float.parseFloat(partes[0]);
                float denominador = Float.parseFloat(partes[1]);
                if (denominador != 0) {
                    this.CASILLA.setText(sincero(numerador / denominador));
                } else {
                    this.CASILLA.setText("División por 0");
                }
            }
        }
    }

    // Función auxiliar para validar si es un número válido
    private boolean esNumero(String s) {
        for (char c : s.toCharArray()) {
            if (!Character.isDigit(c) && c != '.') {
                return false;
            }
        }
        return true;
    }

    // Función para convertir un decimal a fracción
    private String decimalAFraccion(float valor) {
        int numerador = (int) (valor * 10000);
        int denominador = 10000;
        int mcd = mcd(numerador, denominador);
        numerador /= mcd;
        denominador /= mcd;
        return numerador + "/" + denominador;
    }

    // Función para calcular el máximo común divisor (MCD)
    private int mcd(int a, int b) {
        while (b != 0) {
            int temp = b;
            b = a % b;
            a = temp;
        }
    return a;

    }//GEN-LAST:event_jButton20ActionPerformed

