    public String sincero(float resultado){
        String retorno = "";
        retorno = Float.toString(resultado);
        if(resultado%1==0){
            retorno = retorno.substring(0,retorno.length()-2);
        
        }
        return retorno;
    }
    public String convertirAFaccion(double numero) {
    if (numero == (int) numero) {  // Si el número es entero, no es necesario convertir
        return String.valueOf((int) numero);
    }

    int numerador = (int) (numero * 10000);
    int denominador = 10000;

    int gcd = encontrarMCD(numerador, denominador);

    numerador /= gcd;
    denominador /= gcd;

    return numerador + "/" + denominador;
}

private int encontrarMCD(int a, int b) {
    if (b == 0) {
        return a;
    }
    return encontrarMCD(b, a % b);
}

public String sumarFracciones(String fraccion1, String fraccion2) {
    String[] partes1 = fraccion1.split("/");
    String[] partes2 = fraccion2.split("/");

    int numerador1 = Integer.parseInt(partes1[0]);
    int denominador1 = Integer.parseInt(partes1[1]);
    int numerador2 = Integer.parseInt(partes2[0]);
    int denominador2 = Integer.parseInt(partes2[1]);

    int numeradorResultado = (numerador1 * denominador2) + (numerador2 * denominador1);
    int denominadorResultado = denominador1 * denominador2;

    int gcd = encontrarMCD(numeradorResultado, denominadorResultado);

    numeradorResultado /= gcd;
    denominadorResultado /= gcd;

    return numeradorResultado + "/" + denominadorResultado;
}

